    DEALLOCATE(FldVal)
    ! double the size of the array
    ALLOCATE(FldVal(sizeFldVal +10000))
    FldVal(1:sizeFldVal) = FldValCopy
    DEALLOCATE(FldValCopy)
    sizeFldVal = sizeFldVal +10000 !* 2
  END IF
END IF
