INTEGER,PARAMETER :: sfpDrawThru = 2
          ! parameters for supply fan kind
INTEGER,PARAMETER :: sfkVAV = 1
INTEGER,PARAMETER :: sfkCAV = 2
          ! parameters for dehumidification type
