    CALL AddToObjStr('Rate of Defrost Time Fraction Increase','0.024',.TRUE.)
  END IF
  IF (isHeatRecEnthalpy) THEN
    htRecSens75 = StringToReal(FldVal(base + ussHeatRecSenEffOff)) + 0.05
