    CALL AddToObjStr('Rate of Defrost Time Fraction Increase','',.TRUE.)
  END IF
  IF (.NOT. isHeatRecNone) THEN
    IF (isEconoLowLimitBlank) THEN
      CALL AddAlwaysSchedule('5')
    ELSE
      CALL AddAlwaysSchedule(FldVal(base + ussEconoLowTempLimitOff))
