  ENDIF
  CALL AddToObjStr('Maximum Flow Rate {m3/s}','')
  CALL AddToObjStr('Pressure Drop Curve Name','')
    IF (.NOT. isPumpBranch) THEN
      !not using branch pumps so only tower is on the branch
