      CALL AddToObjStr('Component Branch Control Type','Active',.TRUE.)
      CALL AddToStrList(TRIM(FldVal(base + wahpNameOff)) // ' WAHP Supp Heating Coil HW Branch',handleHeatingCoilBranch)
    CASE DEFAULT
