    EXPECT_EQ(state->dataLoopNodes->Node(coolingCoilAirInletNodeIndex).Temp, state->dataLoopNodes->Node(coolingCoilAirOutletNodeIndex).Temp);
    // check that heating coil outlet node is at set point
