    EXPECT_NEAR(thisSys.m_EconoPartLoadRatio, 0.1890, 0.001);
    EXPECT_NEAR(thisSys.m_LowSpeedEconRuntime, 0.8101, 0.001);
    thisSys.calcMixedTempAirSPforEconomizerStagingOperation(*state, 0, firstHVACIteration, zoneLoad);
